import React, { useState } from "react";
import "./PlaceItem.css";
import Card from "../../shared/components/UIElements/Card";
import Button from "../../shared/components/FormElements/Button";
import Modal from "../../shared/components/UIElements/Modal";
imp

const PlaceItem = (props) => {
  const [showMap, setshowMap] = useState(false);
  const openMapHandler = () => setshowMap(true);
  const closeMapHandler = () => setshowMap(false);

  return (
    <React.Fragment>
      <Modal
        show={showMap}
        onCancel={closeMapHandler}
        header={props.address}
        contentClass="place-item__modal-content"
        footerClass="place-item__modal-actions"
        footer={<Button onClick={closeMapHandler}></Button>}
      >
        <div className="map-container">
          <h2>The map</h2>
        </div>
      </Modal>
      <li className="place-item">
        <Card className="place-item__content">
          <div className="place-item__image">
            <img src={props.image} alt={props.tittle} />
          </div>
          <div className="place-item__info">
            <h2>{props.tittle}</h2>
            <h3>{props.address}</h3>
            <p>{props.description}</p>
            {/* <Link to={`/places/${props.id}`} className="place-item__link">View Place</Link> */}
          </div>
          <div className="place-item__actions">
            <Button onClick={openMapHandler} inverse>View on map</Button>
            <Button to={`/places/${props.id}`}>Edit</Button>
            <Button danger>Delete</Button>
          </div>
        </Card>
      </li>
    </React.Fragment>
  );
};

export default PlaceItem;
