import React from 'react';
import { CSSTransition } from 'react-transition-group';
// import './YourTransitionStyles.css'; // Make sure to import or define your CSS transition styles
import ReactDOM from "react-dom";

const SideDrawer = (props) => {
  const content = (
    <CSSTransition
      in={props.show}
      timeout={200} // Corrected the syntax for timeout
      classNames="slide-in-left"
      mountOnEnter
      unmountOnExit // Optional: removes the component from the DOM when not in the 'in' state
    >
      <aside className="side-drawer" oncl>{props.children}</aside>
    </CSSTransition>
  );

  return ReactDOM.createPortal(content, document.getElementById("drawer-hook"));
};

export default SideDrawer;
