import { BrowserRouter as Router, Routes, Route,r } from "react-router-dom";
import User from "./user/components/pages/user";
// import Useritem from "./user/components/useritem";
// import MainNavigation from "./shared/components/Navigation /MainNavigation";
import MainNavigation from "./shared/components/Navigation/MainNavigation";
import UserPlaces from "./places/components/pages/UserPlaces";
import UpdatePlace from "./places/components/pages/UpdatePlace";
import Auth from "./user/components/pages/Auth";
import NewPlace from "./places/components/pages/NewPlace";
import { AuthContext } from "./shared/components/Context/auth.context";
import React, { useCallback, useLayoutEffect, useState, useSyncExternalStore } from "react";

const App = () => {
  const [isLogedIn, setisLogedIn] = useState(false);
  const login = useCallback(() => {
    setisLogedIn(true);
  },[]);
  const logout = useCallback(() => {
    setisLogedIn(false);
  },[]);

 let routes;

 if(isLogedIn) {
  routes = (
    <React.Fragment>
     <Route path="/" element={<User />} />
     <Route path="/:userId/places" element={<UserPlaces />}></Route>
     <Route path="/places/new" element={<NewPlace />}></Route>
     <Route path="/places/:placeId" element={<UpdatePlace />}></Route>
     <Redirect to="/auth"/>
    </React.Fragment>
  ); 
 }else{
  routes = (
    <React.Fragment>
     <Route path="/" element={<User />} />
     <Route path="/:userId/places" element={<UserPlaces />}></Route>
     <Route path="/auth" element={<Auth />}></Route>
     <Redirect to="/auth"/>
    </React.Fragment>
  );
 }

  return (
    <AuthContext.Provider value={{isLogedIn: isLogedIn, login: login, logout: logout}}>
      <Router>
        <MainNavigation />
        <main>
          {/* <Routes>
            <Route path="/" element={<User />} />
            <Route path="/:userId/places" element={<UserPlaces />}></Route>
            <Route path="/places/new" element={<NewPlace />}></Route>
            <Route path="/places/:placeId" element={<UpdatePlace />}></Route>
            <Route path="/auth" element={<Auth />}></Route>
          </Routes> */}
          {routes}
        </main>
      </Router>
    </AuthContext.Provider>
  );
};

export default App;
