import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import User from "./user/components/pages/user";
import MainNavigation from "./shared/components/Navigation/MainNavigation";
import UserPlaces from "./places/components/pages/UserPlaces";
import UpdatePlace from "./places/components/pages/UpdatePlace";
import Auth from "./user/components/pages/Auth";
import { AuthContext } from "./shared/components/Context/auth.context";
import React, { useCallback, useState } from "react";

const App = () => {
  const [isLogedIn, setisLogedIn] = useState(false);

  const login = useCallback(() => {
    setisLogedIn(true);
  }, []);

  const logout = useCallback(() => {
    setisLogedIn(false);
  }, []);

  let routes;

  if (isLogedIn) {
    routes = (
      <Routes>
        <Route path="/" element={<User />} />
        <Route path="/:userId/places" element={<UserPlaces />} />
        <Route path="/places/new" element={<NewPlace />} />
        <Route path="/places/:placeId" element={<UpdatePlace />} />
        <Route path="/auth" element={<Navigate to="/auth" />} />
      </Routes>
    );
  } else {
    routes = (
      <Routes>
        <Route path="/" element={<User />} />
        <Route path="/:userId/places" element={<UserPlaces />} />
        <Route path="/auth" element={<Auth />} />
        <Route path="*" element={<Navigate to="/auth" />} />
      </Routes>
    );
  }

  return (
    <AuthContext.Provider value={{ isLogedIn: isLogedIn, login: login, logout: logout }}>
      <Router>
        <MainNavigation />
        <main>
          {routes}
        </main>
      </Router>
    </AuthContext.Provider>
  );
};

export default App;
